#include <pybind11/complex.h>
#include <pybind11/pybind11.h>
#include <pybind11/stl.h>

namespace py = pybind11;

#include <gnuradio/blocks/recast.h>
// pydoc.h is automatically generated in the build directory
#include <recast_pydoc.h>

void bind_recast(py::module& m)
{

    using recast = ::gr::blocks::recast;


    py::class_<recast, gr::block, gr::basic_block, std::shared_ptr<recast>>(
        m, "recast", D(recast))

        .def(py::init(&recast::make),
             py::arg("input_size"),
             py::arg("output_size"),
             D(recast, make))


        ;
}
