#include <pybind11/complex.h>
#include <pybind11/pybind11.h>
#include <pybind11/stl.h>

namespace py = pybind11;

#include <gnuradio/blocks/typecast.h>
// pydoc.h is automatically generated in the build directory
#include <typecast_pydoc.h>

void bind_typecast(py::module& m)
{

    using typecast = ::gr::blocks::typecast;


    py::class_<typecast, gr::block, gr::basic_block, std::shared_ptr<typecast>>(
        m, "typecast", D(typecast))

        .def(py::init(&typecast::make),
             py::arg("sizeof_in_stream_item"),
             py::arg("sizeof_out_stream_item"),
             D(typecast, make));
}
