#include <pybind11/complex.h>
#include <pybind11/pybind11.h>
#include <pybind11/stl.h>

namespace py = pybind11;

#include <gnuradio/iio/ad9081_source.h>
// pydoc.h is automatically generated in the build directory
#include <ad9081_common_pydoc.h>
#include <ad9081_source_pydoc.h>

void bind_ad9081_source(py::module& m)
{

    using ad9081_source = gr::iio::ad9081_source;
    using ad9081_common = gr::iio::ad9081_common;

    py::class_<ad9081_source, gr::basic_block, std::shared_ptr<ad9081_source>>(
        m, "ad9081_source", D(ad9081_source))

        .def_readonly_static("MAX_CHANNEL_COUNT", &ad9081_common::MAX_CHANNEL_COUNT)
        .def_readonly_static("N_FNCO", &ad9081_common::N_FNCO)
        .def_readonly_static("N_CNCO", &ad9081_common::N_CNCO)

        .def(py::init(&ad9081_source::make),
             py::arg("uri"),
             py::arg("en"),
             py::arg("buffer_size") = 65536UL,
             D(ad9081_source, make))

        .def(
            "set_main_nco_freq",
            [](ad9081_source& src, int nco, int64_t freq) {
                src.set_main_nco_freq(nco, freq);
            },
            py::arg("nco"),
            py::arg("freq"),
            D(ad9081_common, set_main_nco_freq))

        .def(
            "set_main_nco_phase",
            [](ad9081_source& src, int nco, float phase) {
                src.set_main_nco_phase(nco, phase);
            },
            py::arg("nco"),
            py::arg("phase"),
            D(ad9081_common, set_main_nco_phase))

        .def(
            "set_channel_nco_freq",
            [](ad9081_source& src, int nco, int64_t freq) {
                src.set_main_nco_freq(nco, freq);
            },
            py::arg("nco"),
            py::arg("freq"),
            D(ad9081_common, set_channel_nco_freq))

        .def(
            "set_channel_nco_phase",
            [](ad9081_source& src, int nco, float phase) {
                src.set_main_nco_phase(nco, phase);
            },
            py::arg("nco"),
            py::arg("freq"),
            D(ad9081_common, set_channel_nco_phase))

        .def("set_nyquist_zone",
             &ad9081_source::set_nyquist_zone,
             py::arg("odd"),
             D(ad9081_source, set_nyquist_zone))

        .def("set_filter_source_file",
             &ad9081_source::set_filter_source_file,
             py::arg("src_file"),
             D(ad9081_source, set_filter_source_file));


    using ad9081_source_f32c = gr::iio::ad9081_source_f32c;

    py::class_<ad9081_source_f32c, gr::hier_block2, std::shared_ptr<ad9081_source_f32c>>(
        m, "ad9081_source_f32c", D(ad9081_source_f32c))

        .def(py::init(&ad9081_source_f32c::make),
             py::arg("uri"),
             py::arg("en"),
             py::arg("buffer_size") = 65536UL,
             D(ad9081_source_f32c, make))

        .def("set_main_nco_freq",
             &ad9081_source_f32c::set_main_nco_freq,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_source_f32c, set_main_nco_freq))

        .def("set_main_nco_phase",
             &ad9081_source_f32c::set_main_nco_phase,
             py::arg("nco"),
             py::arg("phase"),
             D(ad9081_source_f32c, set_main_nco_phase))

        .def("set_channel_nco_freq",
             &ad9081_source_f32c::set_channel_nco_freq,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_source_f32c, set_channel_nco_freq))

        .def("set_channel_nco_phase",
             &ad9081_source_f32c::set_channel_nco_phase,
             py::arg("nco"),
             py::arg("freq"),
             D(ad9081_source_f32c, set_channel_nco_phase))

        .def("set_nyquist_zone",
             &ad9081_source_f32c::set_nyquist_zone,
             py::arg("odd"),
             D(ad9081_source_f32c, set_nyquist_zone))

        .def("set_filter_source_file",
             &ad9081_source_f32c::set_filter_source_file,
             py::arg("src_file"),
             D(ad9081_source_f32c, set_filter_source_file));
}
